import './Eshopitems.scss';
import Pencil_img from '../../Components/Images/pencil_img.jpg';
import Eraser_img from '../../Components/Images/eraser_img.jpg';
import Notebook_img from '../../Components/Images/notebook_img.jpeg';
import Backpack_img from '../../Components/Images/backpack_img.jpg';
import Pencilpouch_img from '../../Components/Images/pencilpouch_img.jpg';
import Calculator_img from '../../Components/Images/calculator_img.jpeg';
import Item_img from '../../Components/Images/item_img.jpg';
import SectionHeading from '../../Components/SectionHeading/SectionHeading.js';

var addtocart = "Add to cart";
export const esi = [
  {
    itemnumber:1,
    quantity:0,
    image:Item_img,
    name:"Pencil",
    description:"The rocketship to our dreams",
    price:1.00,
    addtocartbutton:addtocart,
  },
  {
    itemnumber:2,
    quantity:0,
    image:Item_img,
    name:"Eraser",
    description:"For the adventurist",
    price:2.00,
    addtocartbutton:addtocart,
  },
  {
    itemnumber:3,
    quantity:0,
    image:Item_img,
    name:"Notebook",
    description:"All the places we've traveled",
    price:6.00,
    addtocartbutton:addtocart,
  },
  {
    itemnumber:4,
    quantity:0,
    image:Item_img,
    name:"Backpack",
    description:"One's personal library",
    price:40.00,
    addtocartbutton:addtocart,
  },
  {
    itemnumber:5,
    quantity:0,
    image:Item_img,
    name:"Pencil Pouch",
    description:"A toolkit of marvelous instruments",
    price:10.00,
    addtocartbutton:addtocart,
  },
  {
    itemnumber:6,
    quantity:0,
    image:Item_img,
    name:"Calculator",
    description:"A centerpiece of mathematics",
    price:25.00,
    addtocartbutton:addtocart,
  },
];

function Eshopitems() {
  return (
    <div className="eshopitems" id="shop">
      <SectionHeading word1="Item" word2="Library" />
      <div className="eshopitems_cards_container">
        {esi.map(i => (
          <div className="eshopitems_cards">
            <div className="eshopitems_cards_contents">
              <span>
                <div className="eshopitems_cards_contents_padding" />
                <img src={i.image} className="eshopitems_cards_contents_image" />
                <div className="eshopitems_cards_contents_name"><span className="smallfont-bigger">{i.name}</span></div>
                <div className="eshopitems_cards_contents_description"><span className="smallfont">{i.description}</span></div>
                <div className="eshopitems_cards_contents_price"><span className="smallfont">{"$" + i.price + ".00"}</span></div>
                <div className="smallfont">
                  <span className="eshopitems_cards_contents_addtocartbutton" onClick={function() {
                    esi[i.itemnumber-1].quantity+=1; {/*alert(i.name + " successfully added to the cart");*/}
                  }}>
                    {i.addtocartbutton}
                  </span>
                </div>
                <div className="eshopitems_cards_contents_padding" />
              </span>
            </div>
          </div>
        ))}
      </div>
    </div>
  )
}


export default Eshopitems;
